mapper.select:


SELECT b.title , b.logo 
FROM blog b, users u 
WHERE  b.user_no = u.no AND u.id = #{id}

mapper.update:

   		UPDATE blog
		SET title= #{title} , logo = #{logo}
		WHERE user_no = (select no from users where id=#{id} )


   		UPDATE blog
		SET title= #{title} 
		WHERE user_no = (select no from users where id=#{id} )


   		UPDATE blog
		SET logo = #{logo}
		WHERE user_no = (select no from users where id=#{id} )
