mapper.select:

 		SELECT no , category_no, user_no, title , content, reg_date
		FROM post
 		WHERE  user_no = (
 			select no
 			from users
 			where id = #{userId}
 		) 
 		ORDER BY reg_date DESC
 		LIMIT 1;


 		SELECT no , category_no, user_no, title , content, reg_date
		FROM post
 		WHERE  user_no = (
 			select no
 			from users
 			where id = #{userId}
 		) AND category_no = #{categoryNo}


 		SELECT no , category_no, user_no, title , content, reg_date
		FROM post
 		WHERE  user_no = (
 			select no
 			from users
 			where id = #{userId}
 		) AND no = #{no}
