mapper.select:

 		SELECT no , category_no, user_no, title , content, reg_date
		FROM post
 		WHERE  user_no = (
 			select no
 			from users
 			where id = #{userId}
 		) AND category_no = #{categoryNo}
 		ORDER BY no desc


 		SELECT no , category_no, user_no, title , content, reg_date
		FROM post
 		WHERE  user_no = (
 			select no
 			from users
 			where id = #{userId}
 		) AND category_no = #{categoryNo}
 		ORDER BY reg_date DESC
 		LIMIT 1;


